from django.db import modelsfrom apps.account.models import Accountfrom apps.logus.models import BookedRoomTariffsModel, BookedRoomRoomModel, AdditionallyPurchasedServicesModel, \    BookedRoomModel, BookingModelclass Payments(models.Model):    PAYMENT_TYPE = (        ('singular', 'singular'),        ('whole_check', 'whole_check')    )    TRANSACTION_TYPE = (        ('Наличка', 'Наличка'),        ('humo', 'humo'),        ('uzcard', 'uzcard'),        ('payme', 'payme'),        ('click', 'click'),        ('uzum', 'uzum'),    )    POCKET_TYPE = (        ('additional service', 'additional service'),        ('room', 'room'),        ('tariff', 'tariff'),        ('medicine', 'medicine'),        ('fee', 'fee'),    )    logus_cheque_id = models.PositiveIntegerField(null=True)    amount = models.BigIntegerField(null=True, blank=True)    pocket_type = models.CharField(max_length=50, choices=POCKET_TYPE, null=True, blank=True)    payment_name = models.TextField(null=True)    comment = models.TextField(null=True, blank=True)    payment_method = models.CharField(choices=PAYMENT_TYPE, max_length=50, default='singular')    transaction_type = models.CharField(choices=TRANSACTION_TYPE, max_length=50, default='Наличка')    status = models.BooleanField(default=True)    booked_room = models.ForeignKey(BookingModel, on_delete=models.SET_NULL, null=True, related_name='payments')    created_by = models.ForeignKey(Account, related_name="payments", on_delete=models.SET_NULL, null=True)    created_at = models.DateTimeField(auto_now_add=True)    modified_at = models.DateTimeField(auto_now=True)    modified_by = models.ForeignKey(Account, related_name="mdf_payments", on_delete=models.SET_NULL, null=True)# class PaymentChequeModel(models.Model):#     payment = models.ForeignKey(Payments, on_delete=models.CASCADE, )#     tariff = models.ForeignKey(BookedRoomTariffsModel, on_delete=models.CASCADE, null=True, blank=True)#     room = models.ForeignKey(BookedRoomRoomModel, on_delete=models.CASCADE, null=True, blank=True)#     service = models.ForeignKey(AdditionallyPurchasedServicesModel, on_delete=models.CASCADE, null=True, blank=True)