from django.shortcuts import render, get_object_or_404, redirectfrom apps.warehouse.forms import AddItemFormfrom apps.warehouse.models import ItemsModeldef items_list(request):    context = {}    items = ItemsModel.objects.all()    context["items"] = items    return render(request, 'old/incomes/F-apteka-income-product.html', context)def item_history(request, pk):    context = {}    item = get_object_or_404(ItemsModel, pk=pk)    context["item"] = item    return render(request, 'old/incomes/item-history.html', context)def post_item(request):    context = {}    if request.POST:        form = AddItemForm(request.POST)        if form.is_valid():            print(request.POST)            is_medicine = bool(request.POST.get("is_medicine", ""))            item_name = request.POST["item_name"]            trade_name = request.POST["trade_name"]            dosage = request.POST["dosage"]            fvip = request.POST["dosage"]            number = request.POST["number"]            company = request.POST["company"]            unit = request.POST["unit"]            barcode = request.POST["barcode"]            ItemsModel.objects.create(name=item_name, unit=unit, )        else:            print(form.errors)    return redirect("warehouse:items-list")