from rest_framework import serializersfrom api.v1.logus.serializers import RoomPriceSerializer, RoomModelSerializerfrom api.v1.organizations.serializers import PatientModelSerializerfrom apps.logus.models import BookedRoomModelclass BookedRoomModelSerializer(serializers.ModelSerializer):    patients = PatientModelSerializer()    room_price = RoomPriceSerializer()    room = RoomModelSerializer()    class Meta:        model = BookedRoomModel        exclude = ["modified_by", "created_by"]