from rest_framework import serializersfrom api.v1.logus.serializers import BookedRoomModelSerializerfrom api.v1.organizations.serializers import PatientModelSerializerfrom apps.logus.models import BookingModelclass BookingModelSerializer(serializers.ModelSerializer):    patient = PatientModelSerializer()    booked_room = BookedRoomModelSerializer()    class Meta:        model = BookingModel        exclude = ["modified_by", "created_by"]class FreeRoomDateRangeSerializer(serializers.Serializer):    start_date = serializers.DateField(required=False, allow_null=True)    end_date = serializers.DateField(required=False, allow_null=True)    class Meta:        fields = '__all__'