from rest_framework import serializersfrom api.v1.logus.serializers import BookedRoomModelSerializerfrom api.v1.organizations.serializers import PatientModelSerializerfrom apps.logus.models import BookingModel, BookedRoomModel, RoomTypeModel, RoomModel# class BookingModelSerializer(serializers.ModelSerializer):#     patient = PatientModelSerializer()#     booked_room = BookedRoomModelSerializer()##     class Meta:#         model = BookingModel#         exclude = ["modified_by", "created_by"]class RoomModelSerializer(serializers.ModelSerializer):    class Meta:        model = RoomModel        fields = '__all__'class RoomTypeModelSerializer(serializers.ModelSerializer):    class Meta:        model = RoomTypeModel        fields = '__all__'class BookingModelSerializer(serializers.ModelSerializer):    patients = PatientModelSerializer()    room = RoomModelSerializer()    room_type = RoomTypeModelSerializer()    class Meta:        model = BookedRoomModel        exclude = ["modified_by", "created_by"]class CreateBookingModelSerializer(serializers.Serializer):    tariff = serializers.CharField()    room = serializers.CharField()    room_type = serializers.CharField()    patients = serializers.IntegerField()    start_date = serializers.DateField(required=True)    end_date = serializers.DateField(required=True)class FreeRoomDateRangeSerializer(serializers.Serializer):    start_date = serializers.DateField(required=False, allow_null=True)    end_date = serializers.DateField(required=False, allow_null=True)    class Meta:        fields = '__all__'