from rest_framework import serializersfrom apps.logus.models import Paymentsclass PaymentCreateSerializer(serializers.ModelSerializer):    # pocket = serializers.ChoiceField(choices=['additional service', 'room', 'tariff', 'medicine', 'fee', ])    # transaction_type = serializers.ChoiceField(choices=['cash', 'humo', 'uzcard', 'payme', 'click', 'uzum', ])    class Meta:        model = Payments        exclude = ["payment_method", ]        read_only_fields = ["modified_by", "created_by", "status"                            ]class PaymentCreateWholeSerializer(serializers.ModelSerializer):    # pocket = serializers.ChoiceField(choices=['additional service', 'room', 'tariff', 'medicine', 'fee', ])    # transaction_type = serializers.ChoiceField(choices=['cash', 'humo', 'uzcard', 'payme', 'click', 'uzum', ])    class Meta:        model = Payments        exclude = ["payment_method", "pocket_type", "logus_cheque_id"]        read_only_fields = ["modified_by", "created_by", "status"                            ]