from rest_framework import serializersfrom apps.logus.models import ServiceModelclass ServiceModelSerializer(serializers.ModelSerializer):    rooms = serializers.SerializerMethodField("rooms_details")    class Meta:        model = ServiceModel        exclude = ["modified_by", "created_by", "modified_at", "created_at", "doctors"]    def rooms_details(self, obj):        context = []        rooms = obj.rooms.all()        for room in rooms:            context.append({"id": room.id, "room_number": room.room_number})        return context