from rest_framework import serializersfrom api.v1.logus.serializers import RoomModelSerializerfrom apps.logus.models import ServiceModelclass ServiceModelSerializer(serializers.ModelSerializer):    rooms = serializers.SerializerMethodField("rooms_details")    class Meta:        model = ServiceModel        exclude = ["modified_by", "created_by", "modified_at", "created_at", "doctors"]    def rooms_details(self, obj):        rooms = obj.rooms.all()        return RoomModelSerializer(rooms, many=True).data