from drf_yasg.utils import swagger_auto_schemafrom rest_framework import statusfrom rest_framework.decorators import api_view, permission_classesfrom rest_framework.permissions import IsAuthenticatedfrom rest_framework.response import Responsefrom django.shortcuts import get_object_or_404from apps.logus.models import RoomModelfrom api.v1.logus.serializers import RoomModelSerializer@permission_classes((IsAuthenticated,))@swagger_auto_schema(method="get", tags=["logus-room"])@api_view(['GET'])def get_room_view(request):    rooms = RoomModel.objects.all()    serializer = RoomModelSerializer(rooms, many=True)    return Response(serializer.data)@permission_classes((IsAuthenticated,))@swagger_auto_schema(method="get", tags=["logus-room"])@api_view(['GET'])def get_room_retrieve_view(request, pk):    room = get_object_or_404(RoomModel, pk=pk)    serializer = RoomModelSerializer(room)    return Response(serializer.data)@permission_classes((IsAuthenticated,))@swagger_auto_schema(method="post", tags=["logus-room"], request_body=RoomModelSerializer)@api_view(['POST'])def create_room_view(request):    room = RoomModel(created_by=request.user, modified_by=request.user)    serializer = RoomModelSerializer(room, data=request.data)    if serializer.is_valid():        serializer.save()        return Response(serializer.data, status=status.HTTP_201_CREATED)    return Response(status=status.HTTP_400_BAD_REQUEST)@permission_classes((IsAuthenticated,))@swagger_auto_schema(method="put", tags=["logus-room"], request_body=RoomModelSerializer)@api_view(['PUT'])def update_room_view(request, pk):    room = get_object_or_404(RoomModel, pk=pk)    room.modified_by = request.user    room.save()    serializer = RoomModelSerializer(room, data=request.data)    if serializer.is_valid():        serializer.save()        return Response(serializer.data, status=status.HTTP_201_CREATED)    return Response(status=status.HTTP_400_BAD_REQUEST)@permission_classes((IsAuthenticated,))@swagger_auto_schema(method="delete", tags=["logus-room"])@api_view(['DELETE'])def delete_room_view(request, pk):    room = get_object_or_404(RoomModel, pk=pk)    room.delete()    return Response(status=status.HTTP_204_NO_CONTENT)