import datetimefrom drf_yasg.utils import swagger_auto_schemafrom rest_framework import statusfrom rest_framework.decorators import api_view, permission_classesfrom rest_framework.permissions import IsAuthenticatedfrom rest_framework.response import Responsefrom django.shortcuts import get_object_or_404from apps.logus.models import RoomTypeModelfrom api.v1.logus.serializers import RoomTypeModelSerializer@permission_classes((IsAuthenticated,))@swagger_auto_schema(method="get", tags=["logus-room-type"])@api_view(['GET'])def get_room_type_view(request):    room_types = RoomTypeModel.objects.all()    serializer = RoomTypeModelSerializer(room_types, many=True)    return Response(serializer.data)@permission_classes((IsAuthenticated,))@swagger_auto_schema(method="get", tags=["logus-room-type"])@api_view(['GET'])def get_room_type_retrieve_view(request, pk):    room_type = get_object_or_404(RoomTypeModel, pk=pk)    serializer = RoomTypeModelSerializer(room_type)    return Response(serializer.data)@permission_classes((IsAuthenticated,))@swagger_auto_schema(method="post", tags=["logus-room-type"], request_body=RoomTypeModelSerializer)@api_view(['POST'])def create_room_type_view(request):    room_type = RoomTypeModel(created_by=request.user, modified_by=request.user)    serializer = RoomTypeModelSerializer(room_type, data=request.data)    if serializer.is_valid():        serializer.save()        return Response(serializer.data, status=status.HTTP_201_CREATED)    return Response(status=status.HTTP_400_BAD_REQUEST)@permission_classes((IsAuthenticated,))@swagger_auto_schema(method="put", tags=["logus-room-type"], request_body=RoomTypeModelSerializer)@api_view(['PUT'])def update_room_type_view(request, pk):    room_type = get_object_or_404(RoomTypeModel, pk=pk)    room_type.modified_by = request.user    room_type.save()    serializer = RoomTypeModelSerializer(room_type, data=request.data)    if serializer.is_valid():        serializer.save()        return Response(serializer.data, status=status.HTTP_201_CREATED)    return Response(status=status.HTTP_400_BAD_REQUEST)@permission_classes((IsAuthenticated,))@swagger_auto_schema(method="delete", tags=["logus-room-type"])@api_view(['DELETE'])def delete_room_type_view(request, pk):    tariff = get_object_or_404(RoomTypeModel, pk=pk)    tariff.delete()    return Response(status=status.HTTP_204_NO_CONTENT)@permission_classes((IsAuthenticated,))@swagger_auto_schema(method="get", tags=["logus-room-type"])@api_view(['GET'])def residents_view(request):    today = datetime.date.today()    types = TypeModel.objects.all()    booked_room = BookingModel.objects.filter(start_date__lt=today, end_date__gt=today)    serializer = BookingModelSerializer(booked_room, many=True)    return Response(serializer.data)