from drf_yasg.utils import swagger_auto_schemafrom rest_framework import statusfrom rest_framework.decorators import api_view, permission_classesfrom rest_framework.permissions import IsAuthenticatedfrom rest_framework.response import Responsefrom django.shortcuts import get_object_or_404from apps.logus.models import ServiceModelfrom api.v1.logus.serializers import ServiceModelSerializer@swagger_auto_schema(method="get", tags=["logus-service"])@api_view(['GET'])@permission_classes((IsAuthenticated,))def get_service_view(request):    services = ServiceModel.objects.all()    serializer = ServiceModelSerializer(services, many=True)    return Response(serializer.data)@swagger_auto_schema(method="get", tags=["logus-service", "sanatorium-schedule"])@api_view(['GET'])@permission_classes((IsAuthenticated,))def get_service_retrieve_view(request, pk):    service = get_object_or_404(ServiceModel, pk=pk)    serializer = ServiceModelSerializer(service)    return Response(serializer.data)@swagger_auto_schema(method="post", tags=["logus-service"], request_body=ServiceModelSerializer)@api_view(['POST'])@permission_classes((IsAuthenticated,))def create_service_view(request):    service = ServiceModel(created_by=request.user, modified_by=request.user)    serializer = ServiceModelSerializer(service, data=request.data)    if serializer.is_valid():        serializer.save()        return Response(serializer.data, status=status.HTTP_201_CREATED)    return Response(status=status.HTTP_400_BAD_REQUEST)@swagger_auto_schema(method="put", tags=["logus-service"], request_body=ServiceModelSerializer)@api_view(['PUT'])@permission_classes((IsAuthenticated,))def update_service_view(request, pk):    service = get_object_or_404(ServiceModel, pk=pk)    service.modified_by = request.user    service.save()    serializer = ServiceModelSerializer(service, data=request.data)    if serializer.is_valid():        serializer.save()        return Response(serializer.data, status=status.HTTP_201_CREATED)    return Response(status=status.HTTP_400_BAD_REQUEST)@swagger_auto_schema(method="delete", tags=["logus-service"])@api_view(['DELETE'])@permission_classes((IsAuthenticated,))def delete_service_view(request, pk):    service = get_object_or_404(ServiceModel, pk=pk)    service.delete()    return Response(status=status.HTTP_204_NO_CONTENT)