from drf_yasg.utils import swagger_auto_schemafrom rest_framework import statusfrom rest_framework.decorators import api_view, permission_classesfrom rest_framework.permissions import IsAuthenticatedfrom rest_framework.response import Responsefrom django.shortcuts import get_object_or_404from apps.logus.models import TariffModel, RoomModelfrom api.v1.logus.serializers import TariffModelSerializer, RoomModelSerializer@swagger_auto_schema(method="get", tags=["logus-tariff"])@api_view(['GET'])@permission_classes((IsAuthenticated,))def get_tariff_view(request):    tariffs = TariffModel.objects.all()    serializer = TariffModelSerializer(tariffs, many=True)    return Response(serializer.data)@swagger_auto_schema(method="get", tags=["logus-tariff"])@api_view(['GET'])@permission_classes((IsAuthenticated,))def get_tariff_retrieve_view(request, pk):    tariff = get_object_or_404(TariffModel, pk=pk)    serializer = TariffModelSerializer(tariff)    return Response(serializer.data)@swagger_auto_schema(method="post", tags=["logus-tariff"], request_body=TariffModelSerializer)@api_view(['POST'])@permission_classes((IsAuthenticated,))def create_tariff_view(request):    tariff = TariffModel(created_by=request.user, modified_by=request.user)    serializer = TariffModelSerializer(tariff, data=request.data)    if serializer.is_valid():        serializer.save()        return Response(serializer.data, status=status.HTTP_201_CREATED)    return Response(status=status.HTTP_400_BAD_REQUEST)@swagger_auto_schema(method="put", tags=["logus-tariff"], request_body=TariffModelSerializer)@api_view(['PUT'])@permission_classes((IsAuthenticated,))def update_tariff_view(request, pk):    tariff = get_object_or_404(TariffModel, pk=pk)    tariff.modified_by = request.user    tariff.save()    serializer = TariffModelSerializer(tariff, data=request.data)    if serializer.is_valid():        serializer.save()        return Response(serializer.data, status=status.HTTP_201_CREATED)    return Response(status=status.HTTP_400_BAD_REQUEST)@swagger_auto_schema(method="delete", tags=["logus-tariff"])@api_view(['DELETE'])@permission_classes((IsAuthenticated,))def delete_tariff_view(request, pk):    tariff = get_object_or_404(TariffModel, pk=pk)    tariff.delete()    return Response(status=status.HTTP_204_NO_CONTENT)@swagger_auto_schema(method="delete", tags=["logus-tariff"])@api_view(['DELETE'])@permission_classes((IsAuthenticated,))def get_tariff_rooms(request, pk):    tariff = get_object_or_404(TariffModel, pk=pk)    rooms = RoomModel.objects.filter(room_type__tariff=tariff)    serializer = RoomModelSerializer(rooms, many=True)    return Response(serializer.data)