from drf_yasg.utils import swagger_auto_schemafrom rest_framework import statusfrom rest_framework.decorators import api_view, permission_classesfrom rest_framework.permissions import IsAuthenticatedfrom rest_framework.response import Responsefrom django.shortcuts import get_object_or_404from apps.logus.models import TariffServiceModelfrom api.v1.logus.serializers import TariffServiceModelSerializer@permission_classes((IsAuthenticated,))@swagger_auto_schema(method="get", tags=["logus-tariff-service"])@api_view(['GET'])def get_tariff_service_view(request):    tariff_service = TariffServiceModel.objects.all()    serializer = TariffServiceModelSerializer(tariff_service, many=True)    return Response(serializer.data)@permission_classes((IsAuthenticated,))@swagger_auto_schema(method="get", tags=["logus-tariff-service"])@api_view(['GET'])def get_tariff_service_retrieve_view(request, pk):    tariff_service = get_object_or_404(TariffServiceModel, pk=pk)    serializer = TariffServiceModelSerializer(tariff_service)    return Response(serializer.data)@permission_classes((IsAuthenticated,))@swagger_auto_schema(method="post", tags=["logus-tariff-service"], request_body=TariffServiceModelSerializer)@api_view(['POST'])def create_tariff_service_view(request):    tariff_service = TariffServiceModel.objects.create(created_by=request.user, modified_by=request.user)    serializer = TariffServiceModelSerializer(tariff_service, data=request.data)    if serializer.is_valid():        serializer.save()        return Response(serializer.data, status=status.HTTP_201_CREATED)    return Response(status=status.HTTP_400_BAD_REQUEST)@permission_classes((IsAuthenticated,))@swagger_auto_schema(method="put", tags=["logus-tariff-service"], request_body=TariffServiceModelSerializer)@api_view(['PUT'])def update_tariff_service_view(request, pk):    tariff_service = get_object_or_404(TariffServiceModel, pk=pk)    tariff_service.modified_by = request.user    tariff_service.save()    serializer = TariffServiceModelSerializer(tariff_service, data=request.data)    if serializer.is_valid():        serializer.save()        return Response(serializer.data, status=status.HTTP_201_CREATED)    return Response(status=status.HTTP_400_BAD_REQUEST)@permission_classes((IsAuthenticated,))@swagger_auto_schema(method="delete", tags=["logus-tariff-service"])@api_view(['DELETE'])def delete_tariff_service_view(request, pk):    tariff_service = get_object_or_404(TariffServiceModel, pk=pk)    tariff_service.delete()    return Response(status=status.HTTP_204_NO_CONTENT)