from drf_yasg.utils import swagger_auto_schemafrom rest_framework import statusfrom rest_framework.decorators import api_view, permission_classesfrom rest_framework.permissions import IsAuthenticatedfrom rest_framework.response import Responsefrom django.shortcuts import get_object_or_404from apps.logus.models import RoomTypeModelfrom api.v1.logus.serializers import RoomTypeModelSerializerfrom apps.logus.models.room import VIEW_CHOICE@swagger_auto_schema(method="get", tags=["logus-type"])@api_view(['GET'])@permission_classes((IsAuthenticated,))def get_type_view(request):    type_obj = RoomTypeModel.objects.all()    serializer = RoomTypeModelSerializer(type_obj, many=True)    return Response(serializer.data)@swagger_auto_schema(method="get", tags=["logus-type"])@api_view(['GET'])@permission_classes((IsAuthenticated,))def get_type_retrieve_view(request, pk):    type_obj = get_object_or_404(RoomTypeModel, pk=pk)    serializer = RoomTypeModelSerializer(type_obj)    return Response(serializer.data)@swagger_auto_schema(method="post", tags=["logus-type"], request_body=RoomTypeModelSerializer)@api_view(['POST'])@permission_classes((IsAuthenticated,))def create_type_view(request):    type_obj = RoomTypeModel.objects.create(created_by=request.user, modified_by=request.user)    serializer = RoomTypeModelSerializer(type_obj, data=request.data)    if serializer.is_valid():        serializer.save()        return Response(serializer.data, status=status.HTTP_201_CREATED)    return Response(status=status.HTTP_400_BAD_REQUEST)@swagger_auto_schema(method="put", tags=["logus-type"], request_body=RoomTypeModelSerializer)@api_view(['PUT'])@permission_classes((IsAuthenticated,))def update_type_view(request, pk):    type_obj = get_object_or_404(RoomTypeModel, pk=pk)    type_obj.modified_by = request.user    type_obj.save()    serializer = RoomTypeModelSerializer(type_obj, data=request.data)    if serializer.is_valid():        serializer.save()        return Response(serializer.data, status=status.HTTP_201_CREATED)    return Response(status=status.HTTP_400_BAD_REQUEST)@swagger_auto_schema(method="delete", tags=["logus-type"])@api_view(['DELETE'])@permission_classes((IsAuthenticated,))def delete_type_view(request, pk):    income_item = get_object_or_404(RoomTypeModel, pk=pk)    income_item.delete()    return Response(status=status.HTTP_204_NO_CONTENT)@swagger_auto_schema(method="get", tags=["choices"])@api_view(['GET'])@permission_classes((IsAuthenticated,))def get_choices(request):    context = dict()    context["view_choices"] = [i[0] for i in VIEW_CHOICE]    return Response(context)