from rest_framework import serializersfrom apps.sanatorium.models import IllnessHistoryclass IllnessHistorySerializer(serializers.ModelSerializer):    class Meta:        model = IllnessHistory        fields = "__all__"class IllnessHistoryUpdateSerializer(serializers.ModelSerializer):    class Meta:        model = IllnessHistory        fields = ["profession", "toxic_factors",                  "tags", "diagnosis", "nurse"                                       "doctor"]class SearchtSerializer(serializers.Serializer):    word = serializers.CharField(max_length=255)