from rest_framework import serializersfrom api.v1.logus.serializers import BookingModelSerializerfrom api.v1.organizations.serializers import PatientModelSerializer, DoctorAccountListSerializerfrom apps.sanatorium.models import IllnessHistory, ProfessionsModel, ToxicFactorsModel, TagsModel, DiagnosisTemplateclass ProfessionModelSerializer(serializers.ModelSerializer):    class Meta:        model = ProfessionsModel        fields = "__all__"class ToxicFactorsModelSerializer(serializers.ModelSerializer):    class Meta:        model = ToxicFactorsModel        fields = "__all__"class TagsModelSerializer(serializers.ModelSerializer):    class Meta:        model = TagsModel        fields = "__all__"class DiagnosisTemplateSerializer(serializers.ModelSerializer):    class Meta:        model = DiagnosisTemplate        fields = "__all__"class IllnessHistorySerializer(serializers.ModelSerializer):    patient = PatientModelSerializer()    booking = BookingModelSerializer()    profession = ProfessionModelSerializer()    toxic_factors = ToxicFactorsModelSerializer(many=True)    tags = TagsModelSerializer(many=True)    diagnosis = DiagnosisTemplateSerializer(many=True)    nurse = DoctorAccountListSerializer(many=True)    doctor = DoctorAccountListSerializer()    class Meta:        model = IllnessHistory        fields = "__all__"class IllnessHistoryUpdateSerializer(serializers.ModelSerializer):    class Meta:        model = IllnessHistory        fields = ["profession", "toxic_factors",                  "tags", "diagnosis", "nurse"                                       "doctor"]class SearchtSerializer(serializers.Serializer):    word = serializers.CharField(max_length=255)