from drf_yasg.utils import swagger_auto_schemafrom rest_framework.decorators import permission_classes, api_viewfrom rest_framework.permissions import IsAuthenticatedfrom api.v1.sanatorium.serializers import SearchSerializer, GetDiagnosisWithCategoryfrom api.v1.sanatorium.services import diagnosis_template_category_services@swagger_auto_schema(tags=['sanatorium-initial-appoinment'], method="post", request_body=SearchSerializer)@api_view(['POST', ])@permission_classes((IsAuthenticated,))def search_diagnosis_with_category(request):    response = diagnosis_template_category_services.search_diagnosis_with_category(request)    return response@swagger_auto_schema(tags=['sanatorium-initial-appoinment'], method="post", request_body=GetDiagnosisWithCategory)@api_view(['POST', ])@permission_classes((IsAuthenticated,))def get_diagnosis_with_category(request):    response = diagnosis_template_category_services.get_diagnosis_with_category(request)    return response@swagger_auto_schema(tags=['sanatorium-initial-appoinment'], method="get", )@api_view(['GET', ])@permission_classes((IsAuthenticated,))def get_diagnosis_with_pk(request, pk):    response = diagnosis_template_category_services.get_diagnosis_with_pk(pk)    return response