from django.db import modelsfrom apps.account.models import PatientModel, Accountclass LogusChequeModel(models.Model):    patient = models.ForeignKey(PatientModel, on_delete=models.CASCADE, related_name='logus_cheque', null=True)    is_paid = models.BooleanField(default=False)    created_by = models.ForeignKey(Account, related_name="logus_cheque", on_delete=models.SET_NULL, null=True)    created_at = models.DateTimeField(auto_now_add=True)    modified_at = models.DateTimeField(auto_now=True)    modified_by = models.ForeignKey(Account, related_name="modf_logus_cheque", on_delete=models.SET_NULL, null=True)    def __str__(self):        return f'{self.patient} - {self.id}'