from django.db import modelsfrom apps.account.models import Accountfrom apps.warehouse.models import ItemsModelINJECTIONS_TYPE = (    ("", ""))class PillsInjections(models.Model):    items = models.ForeignKey(ItemsModel, on_delete=models.CASCADE)    quantity = models.IntegerField()    start_date = models.DateField()    end_date = models.DateField()    days = models.IntegerField()    is_vocation_needed = models.BooleanField()    peer_day = models.IntegerField()    injection_type = models.CharField(null=True, blank=True, choices=INJECTIONS_TYPE)    comments = models.TextField(null=True, blank=True)    doctor = models.ForeignKey(Account, on_delete=models.CASCADE)    def __str__(self):        return self.injection_type