from django.db import modelsfrom apps.account.models import Accountclass Payments(models.Model):    PAYMENT_TYPE = (        ('singular', 'singular'),        ('whole_check', 'whole_check')    )    TRANSACTION_TYPE = (        ('cash', 'Cash'),        ('humo', 'humo'),        ('uzcard', 'uzcad'),        ('payme', 'payme'),        ('click', 'click'),        ('uzum', 'uzum'),    )    POCKET_TYPE = (        ('additional service', 'additional service'),        ('room', 'room'),        ('tariff', 'tariff'),        ('medicine', 'medicine'),        ('fee', 'fee'),    )    logus_cheque_id = models.IntegerField()    amount = models.BigIntegerField()    pocket_type = models.CharField(max_length=50, choices=POCKET_TYPE)    comment = models.TextField()    payment_method = models.CharField(choices=PAYMENT_TYPE, max_length=50)    transaction_type = models.CharField(choices=TRANSACTION_TYPE, max_length=50)    status = models.BooleanField()    created_by = models.ForeignKey(Account, related_name="payments", on_delete=models.SET_NULL, null=True)    created_at = models.DateTimeField(auto_now_add=True)    modified_at = models.DateTimeField(auto_now=True)    modified_by = models.ForeignKey(Account, related_name="mdf_payments", on_delete=models.SET_NULL, null=True)