from django.core.management.base import BaseCommandfrom apps.sanatorium.models import DiagnosisTemplateCategory, InitialAppointmentWithDoctorModelfields = [field.name for field in InitialAppointmentWithDoctorModel._meta.fields]templates = []for field in fields:    if field not in ["doctor", "id"]:        templates.append({"name": field})class Command(BaseCommand):    help = 'sync templates to db'    def handle(self, *args, **options):        for template in templates:            DiagnosisTemplateCategory.objects.get_or_create(name=template["name"])