import uuidfrom django.db import modelsfrom apps.logus.models import BookingModelclass ChequeModel(models.Model):    series = models.CharField(max_length=255, default=uuid.uuid4)    amount = models.IntegerField()    discount = models.IntegerField()    booking = models.ForeignKey(BookingModel, on_delete=models.CASCADE)    # abs_amount = models.IntegerField()    @property    def abs_amount(self):        return self.amount - self.discount