import osfrom datetime import datetimeimport uuidfrom datetime import timedeltafrom django.db import models, transactionfrom django.db.models.signals import post_savefrom django.dispatch import receiverfrom apps.account.models import Account, MedicalService, DoctorAccountModel, NurseAccountModelfrom apps.account.models import PatientModelfrom apps.lis.models import LabResearchModel, upload_locationfrom apps.logus.models import ServiceModel, BookingModelfrom apps.sanatorium.models.diagnosis_template import DiagnosisTemplatefrom apps.warehouse.models import ItemsModel, ItemsInStockModelclass ProfessionsModel(models.Model):    name = models.CharField(max_length=50, blank=True, null=True)    def __str__(self):        return self.nameclass ToxicFactorsModel(models.Model):    name = models.CharField(max_length=50, blank=True, null=True)    def __str__(self):        return self.nameclass TagsModel(models.Model):    name = models.CharField(max_length=50, blank=True, null=True)    def __str__(self):        return self.nameclass IllnessHistory(models.Model):    TYPES = (        ('stationary', 'stationary'),        ('ambulatory', 'ambulatory'),    )    STATE = (        ('open', 'open'),        ('closed', 'closed'),    )    series_number = models.CharField(max_length=255, default=uuid.uuid4)    patient = models.ForeignKey(PatientModel, on_delete=models.CASCADE)    booking = models.ForeignKey(BookingModel, on_delete=models.CASCADE, related_name='illness_history')    type = models.CharField(choices=TYPES, max_length=15, default='stationary')    profession = models.ForeignKey(ProfessionsModel, on_delete=models.SET_NULL, null=True, blank=True)    toxic_factors = models.ManyToManyField(ToxicFactorsModel)    tags = models.ForeignKey(TagsModel, on_delete=models.SET_NULL, null=True, blank=True)    state = models.CharField(choices=STATE, default="open", max_length=100)    initial_diagnosis = models.ForeignKey(DiagnosisTemplate, on_delete=models.CASCADE, null=True, blank=True, related_name='initial')    at_arrival_diagnosis = models.ForeignKey(DiagnosisTemplate, on_delete=models.CASCADE, null=True, blank=True, related_name='arrival')    diagnosis = models.ForeignKey(DiagnosisTemplate, on_delete=models.CASCADE, null=True, blank=True, related_name='illness_history_diagnosis')    nurse = models.ManyToManyField(NurseAccountModel, related_name="illness_history_nurse")    doctor = models.ForeignKey(DoctorAccountModel, on_delete=models.CASCADE, related_name="illness_history_doctor", null=True, blank=True)    def __str__(self):        return str(self.series_number)class BaseMedicalServiceModel(models.Model):    STATE_CHOICES = (        ('assigned', 'assigned'),        ('cancelled', 'cancelled'),        ('stopped', 'stopped'),        ('dispatched', 'dispatched'),    )    illness_history = models.ForeignKey(IllnessHistory, on_delete=models.CASCADE, null=True, blank=True)    medical_service = models.ForeignKey(MedicalService, on_delete=models.SET_NULL, null=True)    price = models.BigIntegerField(default=0)    consulted_doctor = models.ForeignKey(DoctorAccountModel, blank=True, null=True, on_delete=models.SET_NULL,                                         related_name='base_md_consulted_doctor')    state = models.CharField(choices=STATE_CHOICES, default='assigned', max_length=50)    created_by = models.ForeignKey(Account, related_name='b_md_created', on_delete=models.SET_NULL, null=True)    created_at = models.DateTimeField(auto_now_add=True)    modified_at = models.DateTimeField(auto_now=True)    modified_by = models.ForeignKey(Account, related_name="modf_b_md", on_delete=models.SET_NULL, null=True)class BaseProcedureServiceModel(models.Model):    STATE_CHOICES = (        ('assigned', 'assigned'),        ('cancelled', 'cancelled'),        ('stopped', 'stopped'),        ('dispatched', 'dispatched'),    )    FREQUENCY_CHOICES = (        ("–∫–∞–∂–¥—ã–π –¥–µ–Ω—å", "–∫–∞–∂–¥—ã–π –¥–µ–Ω—å"),        ("—á–µ—Ä–µ–∑ –¥–µ–Ω—å", "—á–µ—Ä–µ–∑ –¥–µ–Ω—å"),    )    illness_history = models.ForeignKey(IllnessHistory, on_delete=models.CASCADE, null=True, blank=True)    medical_service = models.ForeignKey(MedicalService, on_delete=models.SET_NULL, null=True)    price = models.BigIntegerField(default=0)    procedure_doctor = models.ForeignKey(DoctorAccountModel, blank=True, null=True, on_delete=models.SET_NULL,                                         related_name='base_procedure_doctor')    state = models.CharField(choices=STATE_CHOICES, default='assigned', max_length=50)    quantity = models.IntegerField(default=1)    done_quantity = models.IntegerField(default=0, null=True)    start_date = models.DateField(null=True)    frequency = models.CharField(choices=FREQUENCY_CHOICES, default='–∫–∞–∂–¥—ã–π –¥–µ–Ω—å', max_length=50)    comments = models.TextField(null=True, blank=True)    created_by = models.ForeignKey(Account, related_name='bp_created', on_delete=models.SET_NULL, null=True)    created_at = models.DateTimeField(auto_now_add=True)    modified_at = models.DateTimeField(auto_now=True)    modified_by = models.ForeignKey(Account, related_name="modf_bp", on_delete=models.SET_NULL, null=True)    def __str__(self):        return f"{self.illness_history.series_number} - {self.medical_service.name}"    @property    def progres_percentile(self):        return int(self.done_quantity / self.quantity*100)    @property    def remaining_quantity(self):        return int(self.quantity - self.done_quantity)    def save(self, *args, **kwargs):        # Get the original state before saving the object.        original_state = None        if self.pk:            original_state = BaseProcedureServiceModel.objects.get(pk=self.pk).state        super().save(*args, **kwargs)        # Check if the state has changed to 'dispatched' and create procedure days if it has.        if self.state == 'dispatched' and original_state != 'dispatched':            self.create_procedure_days()    def create_procedure_days(self):        """        Creates ProcedureDaysModel instances based on the quantity and frequency of the base procedure.        """        if not self.start_date or self.quantity <= 0:            return        for day_index in range(self.quantity):            procedure_start_at = self._get_procedure_start_time(self.start_date, day_index)            is_out_of_graphic = self._is_out_of_graphic(procedure_start_at)            ProcedureDaysModel.objects.create(                procedure=self,                start_at=procedure_start_at,                state='—Å–æ–∑–¥–∞–Ω–æ',                created_by=self.created_by,                is_out_of_graphic=is_out_of_graphic            )    def _get_procedure_start_time(self, start_date: datetime.date, day_offset: int) -> datetime:        """        Returns the start time of the procedure at 9:00 a.m. for the given day offset.        """        start_datetime = datetime.combine(start_date, datetime.min.time()) + timedelta(days=day_offset)        return start_datetime.replace(hour=9, minute=0)    def _is_out_of_graphic(self, start_at: datetime) -> bool:        """        Determines if the given start date is out of the booking end date.        """        return start_at.date() > self.illness_history.booking.end_dateclass ProcedureDaysModel(models.Model):    STATE_CHOICES = (        ('—Å–æ–∑–¥–∞–Ω–æ', '—Å–æ–∑–¥–∞–Ω–æ'),        ('–æ—Ç–º–µ–Ω–µ–Ω–æ', '–æ—Ç–º–µ–Ω–µ–Ω–æ'),        ('–ø—Ä–∏–æ—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω–æ', '–ø—Ä–∏–æ—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω–æ'),        ('–¥–∏—Å–ø–µ—Ç—á–µ—Ä–∏–∑–∏—Ä–æ–≤–∞–Ω', '–¥–∏—Å–ø–µ—Ç—á–µ—Ä–∏–∑–∏—Ä–æ–≤–∞–Ω'),        ('–≤—ã–ø–æ–ª–Ω–µ–Ω–æ', '–≤—ã–ø–æ–ª–Ω–µ–Ω–æ'),    )    state = models.CharField(choices=STATE_CHOICES, default='created', max_length=50)    procedure = models.ForeignKey(BaseProcedureServiceModel, on_delete=models.CASCADE, null=True, blank=True, related_name='days')    start_at = models.DateTimeField(null=True, blank=True)    procedure_doctor = models.ForeignKey(DoctorAccountModel, on_delete=models.SET_NULL, null=True, blank=True)    comments = models.TextField(null=True, blank=True)    is_out_of_graphic = models.BooleanField(default=False, null=True, blank=True)    created_by = models.ForeignKey(Account, related_name='proc_days_created', on_delete=models.SET_NULL, null=True)    created_at = models.DateTimeField(auto_now_add=True)    modified_at = models.DateTimeField(auto_now=True)    modified_by = models.ForeignKey(Account, related_name="proc_days_mod", on_delete=models.SET_NULL, null=True)    def save(self, *args, **kwargs):        # Get the original state before saving the object.        original_state = None        if self.pk:            original_state = ProcedureDaysModel.objects.get(pk=self.pk).state        super().save(*args, **kwargs)        # Increment or decrement the done_quantity of the procedure based on the state change.        if self.state == '–≤—ã–ø–æ–ª–Ω–µ–Ω–æ' and original_state != '–≤—ã–ø–æ–ª–Ω–µ–Ω–æ':            self.procedure.done_quantity += 1        elif original_state == '–≤—ã–ø–æ–ª–Ω–µ–Ω–æ' and self.state != '–≤—ã–ø–æ–ª–Ω–µ–Ω–æ':            self.procedure.done_quantity -= 1        # Persist the done_quantity change to the procedure.        self.procedure.save()class BaseLabResearchServiceModel(models.Model):    STATE_CHOICES = (        ('assigned', 'assigned'),        ('cancelled', 'cancelled'),        ('stopped', 'stopped'),        ('dispatched', 'dispatched'),        ('results', 'results'),    )    illness_history = models.ForeignKey(IllnessHistory, on_delete=models.CASCADE, null=True,                                        related_name='base_lab_research')    lab = models.ForeignKey(LabResearchModel, on_delete=models.SET_NULL, null=True, related_name="lab_app")    price = models.BigIntegerField(default=0)    lab_doctor = models.ForeignKey(DoctorAccountModel, blank=True, null=True, on_delete=models.SET_NULL,                                   related_name='base_lab_doctor')    state = models.CharField(choices=STATE_CHOICES, default='assigned', max_length=50)    start_date = models.DateField(null=True)    comments = models.TextField(null=True, blank=True)    created_by = models.ForeignKey(Account, related_name='bl_created', on_delete=models.SET_NULL, null=True)    created_at = models.DateTimeField(auto_now_add=True)    modified_at = models.DateTimeField(auto_now=True)    modified_by = models.ForeignKey(Account, related_name="modf_bl", on_delete=models.SET_NULL, null=True)class LabResult(models.Model):    base_lab_research = models.ForeignKey(BaseLabResearchServiceModel, related_name='lab_results', on_delete=models.CASCADE, null=True, blank=True)    comments = models.TextField(null=True, blank=True)    result_date = models.DateTimeField(auto_now_add=True)    attached_file = models.FileField(upload_to=upload_location, null=True, blank=True)    file_format = models.CharField(max_length=10, null=True, blank=True)    created_by = models.ForeignKey(Account, related_name='lab_result_created', on_delete=models.SET_NULL, null=True)    created_at = models.DateTimeField(auto_now_add=True)    modified_at = models.DateTimeField(auto_now=True)    modified_by = models.ForeignKey(Account, related_name="lab_result_mdf", on_delete=models.SET_NULL, null=True)    class Meta:        ordering = ['-created_at']  # Most recent results first    def save(self, *args, **kwargs):        # Check if a file is uploaded or updated        if self.attached_file and self.attached_file.name:            # Extract file extension as format            self.file_format = os.path.splitext(self.attached_file.name)[-1].replace('.', '').lower()        super().save(*args, **kwargs)    def __str__(self):        return f"Lab result for {self.base_lab_research} on {self.result_date}"class BasePillsInjectionsModel(models.Model):    STATE_CHOICES = (        ('assigned', 'assigned'),        ('cancelled', 'cancelled'),        ('stopped', 'stopped'),        ('dispatched', 'dispatched'),    )    PILLS_FREQUENCY_CHOICES = (        ("3 —Ä–∞–∑–∞ –≤ –¥–µ–Ω—å", 3),        ("–¥–æ –µ–¥—ã", 1),        ("–ø–æ—Å–ª–µ –µ–¥—ã", 1),    )    illness_history = models.ForeignKey(IllnessHistory, on_delete=models.CASCADE,                                        null=True, related_name='base_pills_injections')    state = models.CharField(choices=STATE_CHOICES, default='assigned', max_length=50)    quantity_per_session = models.IntegerField(default=1)    period_days = models.IntegerField(null=True, blank=True)    start_date = models.DateField(null=True)    end_date = models.DateField(null=True)    frequency = models.CharField(choices=PILLS_FREQUENCY_CHOICES, default='', max_length=50)    comments = models.TextField(null=True, blank=True)    instruction = models.TextField(null=True, blank=True)    created_by = models.ForeignKey(Account, related_name='b_pl_created', on_delete=models.SET_NULL, null=True)    created_at = models.DateTimeField(auto_now_add=True)    modified_at = models.DateTimeField(auto_now=True)    modified_by = models.ForeignKey(Account, related_name="modf_b_pl", on_delete=models.SET_NULL, null=True)    def __str__(self):        return f"{self.illness_history} - {self.pills_injections}"    # def save(self, *args, **kwargs):    #     if self.pk:    #         # The instance already exists, so it's an update    #         previous_instance = BasePillsInjectionsModel.objects.get(pk=self.pk)    #         previous_quantity = previous_instance.quantity_per_session * previous_instance.period_days    #         quantity_difference = self.quantity - previous_instance.quantity    #     else:    #         # The instance is new, so it's a creation    #         quantity_difference = self.quantity    #    #     # Update the quantity of the item in stock    #     if self.pills_injections:   